#include "common.hh"

// https://leetcode.com/problems/3sum-with-multiplicity/

int threeSumMulti(vector<int>& A, int target) {
    unordered_map<int, vector<int> > num_idx;
    for (int i = 0; i < A.size(); i++) {
        num_idx[A[i]].push_back(i);
    }

    long count = 0;
    for (int i = 0; i < A.size() - 2; i++) {
        for (int j = i + 1; j < A.size() - 1; j++) {
            int remainder = target - (A[i] + A[j]);
            auto it = num_idx.find(remainder);
            if (it != num_idx.end()) {
                const auto& vec = it->second;
                auto u = upper_bound(vec.begin(), vec.end(), j);
                long num_match = vec.end() - u;
                count += num_match;
            }
        }
    }

    return count;
}

int threeSumMultiplicity(vector<int>& A, int target) {
    unordered_map<int, int> num_count;
    for (auto elem : A) {
        num_count[elem]++;
    }

    long long result = 0;
    for (auto p1 : num_count) {
        for (auto p2 : num_count) {
            int a = p1.first;
            int b = p2.first;

            int c = target - (a + b);
            if (num_count.count(c) == 0) {
                continue;
            }
            if (a == b && b == c) {
                result += (num_count[a] * (num_count[a] - 1) * (num_count[a] - 2)) / 6;
            } else if (a == b && b != c) {
                result += (num_count[a] * (num_count[a] - 1)) / 2 * num_count[c];
            } else if (a != b && b != c) {
                result += num_count[a] * num_count[b] * num_count[c];
            }
        }
    }

    return result;

}

